import { ColoredMessage } from "./components/ColoredMessage";

export const App = () => {
    // ボタンを推したときに実行する関数を定義
    const onClickButton = () => {
        alert();
    };


    // ピンク用に追加
    const contenPinkStyle = {
        color: "pink",
        fontSize: "20px"
    };

    return (
        <>
        <h1 style={{ color: "red"}}> こんにちは! </h1>
        <ColoredMessage color="blue" />
        <p style={contenPinkStyle}> 元気です！ </p>
        <button onClick={onClickButton}> ボタン </button>
        </>
        );
    };
